    return (
        <aside className={style.listaTarefas}>
            <h2 onClick={() => {
                setTarefas ([...tarefas, { tarefa: "Estudar estado", tempo: "05:00:00"}])
            }}> Estudos do dia </h2>
            <ul>
                {tarefas.map((item, index) => (
                    <Item 
                        key={index}
                        {...item}                  
                    />
                ))}

            </ul>

        </aside>
    )
}

export default Lista;
